import { ADD_MUSIC } from "../contexts/constants";

export const musicReducer = (state, action) => {
    const { type, payload } = action;
    switch (type) {
        case ADD_MUSIC:
            return {
                ...state,
                musics: [...state.musics, payload],
            };
        case POSTS_LOADED_SUCCESS:
            return {
                ...state,
                posts: payload,
                postsLoading: false,
            };

        case POSTS_LOADED_FAIL:
            return {
                ...state,
                posts: [],
                postsLoading: false,
            };

        default:
            return state;
    }
};
