import React from "react";
import MusicPage from "./component/musicPage";
import Nav from "./component/nav";
import OverviewBoxes from "./component/overviewBoxes";
import PostPage from "./component/postPage";
import SaleBox from "./component/saleBox";
import SideBar from "./component/sideBar";

export default function Admin() {
  const popUp = {
    handlePopup: function () {
      const popupBtn = document.querySelector("#outbtn");
      const popUpEdit = document.querySelector(".edit-popup");
      const editBtn = document.querySelectorAll(".item-create");
      editBtn.forEach(function (e) {
        e.onclick = function () {
          popUpEdit.classList.add("active");
        };
      });
      popupBtn.onclick = function () {
        popUpEdit.classList.remove("active");
      };
    },
    start: function () {
      this.handlePopup();
    },
  };
  popUp.start();
  return (
    <div className="admin">
      <SideBar />
      <section className="home-section">
        <Nav />
        {/* dashboard page admin */}
        <div className="home-content menu-bar active">
          <OverviewBoxes />
          <SaleBox />
        </div>
        {/* music page admin */}
        <MusicPage />
        {/* post page admin */}
        <PostPage />
      </section>
    </div>
  );
}
