import axios from "axios";
import { createContext, useReducer } from "react";
import { commentReducer } from "../reducers/cmtReducer";
import {
  apiUrl,
  COMMENTS_LOADED_FAIL,
  COMMENTS_LOADED_SUCCESS,
} from "./constants";

export const CommentContext = createContext();

const CommentContextProvider = ({ children }) => {
  // State
  const [commentState, dispatch] = useReducer(commentReducer, {
    comment: {},
    comments: [],
    commentsLoading: true,
  });

  // Get all posts
  const getComments = async () => {
    try {
      const response = await axios.get(`${apiUrl}/comments/datacmts`);
      if (response.data.success) {
        dispatch({
          type: COMMENTS_LOADED_SUCCESS,
          payload: response.data.comments,
        });
      }
    } catch (error) {
      dispatch({ type: COMMENTS_LOADED_FAIL });
    }
  };

  //   // Find id comment when user is updating post
  //   const findIDComment = (commentId) => {
  //     const comment = commentState.comments.find(
  //       (comment) => comment._id === commentId
  //     );
  //     dispatch({ type: COMMENT_CLICK, payload: comment });
  //   };

  //   useEffect(() => getComments(), []);
  //   // Find id comment when user click play comment
  //   const getIdCommentHome = (commentIdHome) => {
  //     const commentGet = commentState.comments.find(
  //       (comment) => comment._id === commentIdHome
  //     );
  //     dispatch({
  //       type: COMMENT_CLICK_HOME,
  //       payload: commentGet,
  //     });
  //   };

  // comment context data
  const commentContextData = {
    commentState,
    getComments,
    // findIDComment,
    // getIdCommentHome,
  };
  return (
    <CommentContext.Provider value={commentContextData}>
      {" "}
      {children}{" "}
    </CommentContext.Provider>
  );
};

export default CommentContextProvider;
