import { useContext } from "react";
import {
    ADD_MUSIC,
    DELETE_MUSIC,
    MUSICS_LOADED_FAIL,
    MUSICS_LOADED_SUCCESS,
    MUSIC_CLICK,
    MUSIC_CLICK_HOME,
} from "../contexts/constants";
import { MusicContext } from "../contexts/musicContext";

export const musicReducer = (state, action) => {
    const { musicsState } = useContext(MusicContext);
    const { type, payload } = action;
    const musicFirst = musicState.musics[musicState.musics.length - 1];
    console.log(musicFirst);
    switch (type) {
        case ADD_MUSIC:
            return {
                ...state,
                musics: [...state.musics, payload],
            };

        case MUSIC_CLICK:
            return {...state, music: payload };

        case MUSIC_CLICK_HOME:
            return {
                ...state,
                musicHome: payload,
                musicsLoading: true,
            };

        case DELETE_MUSIC:
            return {
                ...state,
                musics: state.musics.filter((music) => music._id !== payload),
            };
        case MUSICS_LOADED_SUCCESS:
            return {
                ...state,
                musics: payload,
                musicsLoading: false,
            };

        case MUSICS_LOADED_FAIL:
            return {
                ...state,
                musics: [],
                musicsLoading: false,
            };

        default:
            return state;
    }
};
