        //   if (isAuthenticated) {
        //     <>
        //       <Component {...rest} {...props} />{" "}
        //     </>;
        //   } else {
        //     <Redirect to="/login" />;
        //   }
        // }

        (props) =>
          isAuthenticated ? (
            <>
              <Component {...rest} {...props} />{" "}
            </>
          ) : (
            <Redirect to="/login" />
          )
      }
    />
  );
};
export default CheckRedirect;
