import React from "react";
import Header from "../../component/header";
import Notification from "../../component/notification";
import MenuFooter from "./component/menuFooter";
import MenuMain from "./component/menuMain";
import MusicFooter from "./component/musicFooter";
import MusicPopup from "./component/musicPopup";
import PostCreate from "./component/postCreate";
import PostDisplay from "./component/postDisplay";
import PostEdit from "./component/PostEdit";

import PostFavorite from "./component/postFavorite";
import PostPerson from "./component/postPerson";

export default function Home() {
  return (
    <div className="home">
      <Header />
      {/* thông báo người dùng */}
      <Notification />
      {/* main */}
      <main className="main">
        <div className="container-fluid">
          <MenuMain />
          <section className="post">
            {/* home page */}
            <PostDisplay />
            {/* my personal page */}
            <PostPerson />
            {/* edit information */}
            <PostEdit />
            {/* my favorite page */}
            <PostFavorite />
            {/* create page */}
            <PostCreate />
          </section>
          <section className="music">
            <div className="music__title">
              <h4>Music is playing</h4>
            </div>
            <div className="music__audio">
              <div className="music__audio-postcard">
                <div className="player">
                  <img
                    src="/img/img_music/2.jpg"
                    className="player__img"
                    alt=""
                  />
                </div>
                <h4 className="player-title tilte">EDM</h4>
                <span className="player-author author">Trung Vũ</span>
                <div className="progress">
                  <input
                    id="progress__input"
                    type="range"
                    defaultValue={0}
                    step={1}
                    min={0}
                    max={100}
                  />
                  <audio src="/music/edm.mp3" className=" progress__song" />
                  {/* <span id="progress__time">0</span> */}
                </div>
              </div>
              <div className="music__audio-btn">
                <i className="fa fa-backward player-prev" />
                <i className="fa fa-play player-play" />
                <i className="fa fa-forward player-next" />
              </div>
            </div>
          </section>
          {/* section music for table, smart phone */}
          <MusicFooter />
          <MenuFooter />
          <MusicPopup />
        </div>
      </main>
    </div>
  );
}
