import axios from "axios";
import React, { useContext, useState } from "react";
import { useHistory } from "react-router";
import { AuthContext } from "../../contexts/authContext";
import { apiUrl, LOCAL_STORAGE_TOKEN_NAME } from "../../contexts/constants";

export default function Register() {
  // Context
  const { loadUser } = useContext(AuthContext);

  // local state
  const [register, setRegister] = useState({
    userEmail: "",
    userPassword: "",
    confirmPassword: "",
    userName: "",
    userAvatar: "",
  });

  //Router
  const history = useHistory();

  // get data as user input
  const onChangeRegisterForm = function (event) {
    setRegister({ ...register, [event.target.name]: event.target.value });
  };

  //get file as user input
  const onChangeFileUserForm = function (event) {
    setRegister({
      userAvatar: event.target.files[0],
    });
    console.log(event.target.files[0]);
    // if (event.target.files && event.target.files[0]) {
    //   let reader = new FileReader();
    //   let file = event.target.files[0];
    //   reader.onloadend = () => {
    //     setRegister({
    //       ...register,
    //       userAvatar: file,
    //     });
    //   };
    //   reader.readAsDataURL(file);
  };

  // when the data changes, add new data for register state
  const { userEmail, userPassword, confirmPassword, userName } = register;

  const userRegister = (e) => {
    const registerData = userRegister(register);
    console.log(registerData);
    e.preventDefault();

    const formData = new FormData();
    formData.append("userEmail", register.userEmail);
    formData.append("userPassword", register.userPassword);
    formData.append("confirmPassword", register.confirmPassword);
    formData.append("userName", register.userName);
    formData.append("userAvatar", register.userAvatar);

    axios
      .post(`${apiUrl}/auth/register`, formData)
      .then((response) => {
        console.log(response);
        if (response.data.success)
          localStorage.setItem(
            LOCAL_STORAGE_TOKEN_NAME,
            response.data.accessToken
          );

        loadUser();

        return response.data;
      })
      .catch((error) => {
        if (error.response.data) return error.response.data;
        else return { success: false, message: error.message };
      });

    // const registerUser = async (formData) => {
    //   try {
    //     const response = await axios.post(`${apiUrl}/auth/register`, formData);
    //     console.log(register);

    //     if (response.data.success)
    //       localStorage.setItem(
    //         LOCAL_STORAGE_TOKEN_NAME,
    //         response.data.accessToken
    //       );

    //     await loadUser();

    //     return response.data;
    //   } catch (error) {
    //     if (error.response.data) return error.response.data;
    //     else return { success: false, message: error.message };
    //   }
    // };

    // if (userPassword != confirmPassword) {
    //   alert("Passwords do not match");
    // }

    // try {
    //   const registerData = await registerUser(register);
    //   if (!registerData.success) {
    //     alert("Registration failed");
    //     console.log(registerData.message);
    //   }
    //   history.push("/login");
    // } catch (e) {
    //   console.log(e);
    // }
  };

  return (
    <div className="register">
      <div className="l-form">
        <form
          onSubmit={userRegister}
          enctype="multipart/form-data"
          className="form"
        >
          <img src="/img/logo1.svg" alt="" />
          <h2 className="form__title">Register</h2>
          <div className="form__div">
            <input
              type="text"
              className="form__input"
              placeholder=" "
              name="userEmail"
              value={userEmail}
              required
              onChange={(event) => {
                onChangeRegisterForm(event);
              }}
            />
            <label htmlFor className="form__label">
              Email
            </label>
          </div>
          <div className="form__div">
            <input
              type="password"
              className="form__input"
              placeholder=" "
              name="userPassword"
              value={userPassword}
              required
              onChange={onChangeRegisterForm}
            />
            <label htmlFor className="form__label">
              Password
            </label>
          </div>
          <div className="form__div">
            <input
              type="password"
              className="form__input"
              placeholder=" "
              name="confirmPassword"
              value={confirmPassword}
              required
              onChange={onChangeRegisterForm}
            />
            <label htmlFor className="form__label">
              Confirm Password
            </label>
          </div>
          <div className="form__div">
            <input
              type="text"
              className="form__input"
              placeholder=" "
              name="userName"
              value={userName}
              required
              onChange={onChangeRegisterForm}
            />
            <label htmlFor className="form__label">
              Your name
            </label>
          </div>
          <div className="form__div">
            <input
              type="file"
              className="form__input"
              onChange={onChangeFileUserForm}
            />
            <label htmlFor className="form__label">
              Your avartar
            </label>
          </div>
          <input
            type="submit"
            className="form__button"
            defaultValue="Sign In"
            value="Register"
          />
        </form>
      </div>
    </div>
  );
}
