import React, { useContext } from "react";
import { AuthContext } from "../contexts/authContext";
import { apiUpload } from "../contexts/constants";

export default function Header() {
  const {
    authState: {
      user: { userName, userAvatar },
    },
    logoutUser,
  } = useContext(AuthContext);

  const history = useHistory();

  const logout = () => {
    logoutUser();
    history;
  };

  return (
    <header className="header__main">
      <div className="container-fluid">
        <div className="logo">
          <a href="/home">
            <img src="/img/logo1.svg" alt="" />
          </a>
        </div>
        <div className="search">
          <i className="search-icon fa fa-search" />
          <input
            type="text"
            inputMode="search"
            placeholder="Search for song, arties etc..."
            className="search-input"
          />
        </div>
        <div className="right">
          <div className="user">
            <img
              src={`${apiUpload}${userAvatar}`}
              alt=""
              className="user__avatar"
            />
            <div className="user__name">
              <h2 className="user__name-title">{userName}</h2>
            </div>
          </div>
          <div id="notiBtn">
            <i className="fa fa-bell tool-icon" />
          </div>
          <a onClick={logout} className="btn1">
            Log out
          </a>
        </div>
      </div>
    </header>
  );
}
