import React, { useContext, useRef, useState } from "react";
import { apiUploadFileMp3 } from "../../../contexts/constants";
import { MusicContext } from "../../../contexts/musicContext";

export default function Music() {
  // get data musicHome at MusicContext
  const {
    musicState: {
      musicHome: { musicFile },
      musics,
    },
  } = useContext(MusicContext);
  const lastMusic = musics[musics.length - 1];

  const [musicFirst, setmusicFirst] = useState(lastMusic.musicFile);
  console.log(musicFirst);
  // const [musicPlaying, setMusicPlaying] = useState(false);
  // const urlMusic = document.querySelector(".progress__song");
  // const playBtn = document.querySelector(".player-play");
  // const songImg = document.querySelector(".player__img");

  // // click play button
  // function playMusic() {
  //   if (!musicPlaying) {
  //     urlMusic.play();
  //     playBtn.classList.add("fa-pause");
  //     setMusicPlaying(!musicPlaying);
  //   } else {
  //     urlMusic.pause();
  //     playBtn.classList.remove("fa-pause");
  //     setMusicPlaying(!musicPlaying);
  //   }
  // }
  const playBtn = document.querySelector(".player-play");
  const urlMusic = document.querySelector(".progress__song");
  const [audioIndex, setAudioIndex] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [isPlay, setPlay] = useState(false);

  const handleLoadedData = () => {
    setDuration(urlMusic.duration);
    if (isPlay) urlMusic.play();
  };

  const handlePausePlayClick = () => {
    if (isPlay) {
      urlMusic.pause();
      playBtn.classList.remove("fa-pause");
      setmusicFirst(musicFile);
    } else {
      urlMusic.play();
      playBtn.classList.add("fa-pause");
      setmusicFirst(musicFile);
    }
    setPlay(!isPlay);
  };
  return (
    <section className="music">
      <div className="music__title">
        <h4>Music is playing</h4>
      </div>
      <div className="music__audio">
        <div className="music__audio-postcard">
          <div className="player">
            <img src="/img/img_music/2.jpg" className="player__img" alt="" />
          </div>
          <h4 className="player-title tilte">EDM</h4>
          <span className="player-author author">Trung VÅ©</span>
          <div className="progress">
            <input
              id="progress__input"
              type="range"
              defaultValue={0}
              step={1}
              min={0}
              max={100}
            />
            <audio
              onLoadedData={handleLoadedData}
              src={`${apiUploadFileMp3}${musicFirst}`}
              className=" progress__song"
            />
            {/* <span id="progress__time">0</span> */}
          </div>
        </div>
        <div className="music__audio-btn">
          <i className="fa fa-backward player-prev" />
          <i
            onClick={handlePausePlayClick}
            className="fa fa-play player-play"
          />
          <i className="fa fa-forward player-next" />
        </div>
      </div>
    </section>
  );
}
