import React, { useContext, useEffect } from "react";
import { AuthContext } from "../../../contexts/authContext";
import { FavoriteContext } from "../../../contexts/farvoriteContext";

export default function PostFavorite() {
  // get global data by useContext
  const {
    favoriteState: { favorites, favorite, favoritessLoading },
    getFavorites,
  } = useContext(FavoriteContext);
  const {
    authState: {
      user: { _id: userId },
    },
  } = useContext(AuthContext);

  // start get all posts
  useEffect(() => getFavorites(), []);
  console.log(userId);

  // check id for render post data
  const checkIdFavorite = favorites.filter((post) => {
    return post.user._id === userId;
  });
  if (postsLoading) {
    return (
      <div style={{ width: "100%", height: "100vh", position: "absolute" }}>
        <Ripple
          style={{
            top: "50%",
            left: "50%",
            position: "relative",
            transform: "translate(-50%, -50%)",
          }}
          color="#be97e8"
        />{" "}
        ;{" "}
      </div>
    );
  } else {
    return (
      <div className="postfavrorite post-list post-list-3">
        <div className="post__items">
          <div className="favorites">
            <h3 className="title">My favorite playlist</h3>
            <div className="like">
              <img
                src="/img/img_music/khong_loi/acoustic/buddy.jpg"
                className="img"
                alt=""
              />
              <h4 className="name">EDM</h4>
              <h4 className="author">Chưa biết tên</h4>
              <a href="#">
                <i className="fa fa-heart" />
              </a>
              <audio
                className="audio"
                src="/music/khong_loi/acoustic/bensound-buddy.mp3"
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
