import { useContext } from "react";
import { Redirect, Route } from "react-router";
import { AuthContext } from "../../contexts/authContext";
import Ripple from "@bit/joshk.react-spinners-css.ripple";
import jwt_decode from "jwt-decode";
import { LOCAL_STORAGE_TOKEN_NAME } from "../../contexts/constants";

const ProtectAdmin = ({ component: Component, ...rest }) => {
    const {
        authState: { authLoading, isAuthenticated },
    } = useContext(AuthContext);

    const decoded = jwt_decode(localStorage[LOCAL_STORAGE_TOKEN_NAME]);

    console.log(decoded.userId);

    if (authLoading) {
        return ( <
            div style = {
                { width: "100%", height: "100vh", position: "absolute" } } >
            <
            Ripple style = {
                {
                    top: "50%",
                    left: "50%",
                    position: "relative",
                    transform: "translate(-50%, -50%)",
                }
            }
            color = "#be97e8" /
            > { " " }; { " " } <
            /div>
        );
    }
    if (decoded.userId === "61600aa78b331c8efe5a3d84") {
        return ( <
            Route {...rest }
            render = {
                (props) =>
                isAuthenticated ? ( <
                    >
                    <
                    Component {...rest } {...props }
                    />{" "} <
                    />
                ) : ( <
                    Redirect to = "/login" / >
                )
            }
            />
        );
    }
    return <Redirect to = "/login" / > ;
};

export default ProtectAdmin;
