import React, { useContext, useState } from "react";
import { useHistory } from "react-router";
import { AuthContext } from "../../contexts/authContext";

export default function Register() {
  // Context
  const { registerUser } = useContext(AuthContext);

  // Local state
  const [registerForm, setRegisterForm] = useState({
    userEmail: "",
    userPassword: "",
    confirmPassword: "",
    userName: "",
    userAvatar: "",
  });

  const history = useHistory();

  const [alertRegister, setAlertRegister] = useState(null);

  // When the data changes, the login changes accordingly
  const { userEmail, userPassword, confirmPassword, userName, userAvatar } =
    registerForm;
  // event when user entered data
  const onChangeRegisterForm = (event) =>
    setRegisterForm({
      ...registerForm,
      [event.target.name]: event.target.value,
    });

  const userRegister = async (event) => {
    event.preventDefault();

    console.log("test");

    if (userPassword !== confirmPassword) {
      setAlertRegister({ type: "danger", message: "Passwords do not match" });
      setTimeout(() => setAlertRegister(null), 5000);
      return alert("Passwords do not match");
    }

    try {
      const registerData = await registerUser(registerForm);
      if (!registerData.success) {
        setAlertRegister({ type: "danger", message: registerData.message });
        setTimeout(() => setAlertRegister(null), 5000);
      }
      history.push("/login");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="register">
      <div className="l-form">
        <form onSubmit={userRegister} className="form">
          <img src="/img/logo1.svg" alt="" />
          <h2 className="form__title">Register</h2>
          <div className="form__div">
            <input
              type="text"
              className="form__input"
              placeholder=" "
              name="userEmail"
              value={userEmail}
              required
              onChange={onChangeRegisterForm}
            />
            <label htmlFor className="form__label">
              Email
            </label>
          </div>
          <div className="form__div">
            <input
              type="password"
              className="form__input"
              placeholder=" "
              name="userPassword"
              value={userPassword}
              required
              onChange={onChangeRegisterForm}
            />
            <label htmlFor className="form__label">
              Password
            </label>
          </div>
          <div className="form__div">
            <input
              type="password"
              className="form__input"
              placeholder=" "
              name="confirmPassword"
              value={confirmPassword}
              required
              onChange={onChangeRegisterForm}
            />
            <label htmlFor className="form__label">
              Confirm Password
            </label>
          </div>
          <div className="form__div">
            <input
              type="text"
              className="form__input"
              placeholder=" "
              name="userName"
              value={userName}
              required
              onChange={onChangeRegisterForm}
            />
            <label htmlFor className="form__label">
              Your name
            </label>
          </div>
          <div className="form__div">
            <input
              type="file"
              className="form__input"
              placeholder=" "
              name="userAvatar"
              value={userAvatar}
              required
              onChange={onChangeRegisterForm}
            />
            <label htmlFor className="form__label">
              Your avartar
            </label>
          </div>
          <input
            type="submit"
            className="form__button"
            defaultValue="Sign In"
            value="Register"
          />
        </form>
      </div>
    </div>
  );
}
