import { ADD_MUSIC, UPDATE_USER } from "../contexts/constants";

export const musicReducer = (state, action) => {
    const { type, payload } = action;
    switch (type) {
        case ADD_MUSIC:
            return {
                ...state,
                musics: [...state.musics, payload],
            };

            return {
                ...state,
                users: newUsers,
            };

        default:
            return state;
    }
};
